import React, { useState } from 'react';
import './styles/App.css';

function LoginPage({ onLogin }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  return (
    <div className="login-page">
      <div className="login-card">
        <h2>Welcome Back</h2>
        <form
          onSubmit={e => {
            e.preventDefault();
            onLogin(username);
          }}
        >
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);

  if (!user) {
    return <LoginPage onLogin={setUser} />;
  }

  return (
    <div className="App">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Your inbox</h2>
        <ul>
          <li className="active">Luis - Github</li>
          <li>Ivan - Nike</li>
          <li>Lead from New York</li>
          <li>Booking API problems</li>
          <li>Miracle - Exemplary Bank</li>
        </ul>
      </div>

      {/* Main Conversation Area */}
      <div className="main-content">
        <div className="conversation-header">
          <h2>Luis Easton</h2>
        </div>
        <div className="conversation">
          <div className="message customer">
            <p>
              I bought a product from your store in November as a Christmas gift for a member of my family. However, it turns out they have something very similar already. I was hoping you'd be able to refund me, as it is un-opened.
            </p>
            <span className="timestamp">1min</span>
          </div>
          <div className="message agent">
            <p>Let me just look into this for you, Luis.</p>
            <span className="timestamp">Seen 1min</span>
          </div>
        </div>
        <div className="chat-input">
          <input type="text" placeholder="Use âŒ˜K for shortcuts" />
          <button>Send</button>
        </div>
      </div>

      {/* AI Copilot Sidebar */}
      <div className="ai-copilot">
        <h2>Hi, I'm Fin AI Copilot</h2>
        <p>Ask me anything about this conversation.</p>
        <div className="suggested-question">
          <button>How do I get a refund?</button>
        </div>
        <input type="text" placeholder="Ask a question..." />
      </div>
    </div>
  );
}

export default App;
